using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;public class TabSwitcher : MonoBehaviour{    [SerializeField]    private List<GameObject> activeObjs;    [SerializeField]    private List<GameObject> unactiveObjs;    public System.Action<int> OnSwitchTabCallback;    private void OnSwitch(int index)    {        for (int i = 0; i < activeObjs.Count; i++)        {            activeObjs[i].SetActive(i == index);            if (i < unactiveObjs.Count)            {                unactiveObjs[i].SetActive(i != index);            }        }        if (OnSwitchTabCallback != null)            OnSwitchTabCallback(index);    }    public void SwitchTab(int index)    {        OnSwitch(index);    }}